jQuery(function($){
	$(".menu_toggle_btn").click(function(){
		$('.gnb').stop().slideToggle('fast');
	});

	var justi = typeof document.createElement('div').style.justifyContent !== 'undefined';
	var wJusti = typeof document.createElement('div').style.WebkitJustifyContent !== 'undefined';

	var link = document.createElement('link');
	link.rel = 'stylesheet';
	link.type = 'text/css';
	var uFlex = 'css/default.css';
	var nFlex = 'css/default_old.css';

	if(justi || wJusti){
		link.href = uFlex;
	}else{
		link.href = nFlex;
	}

	$("head").prepend(link);
});